import React, { ChangeEvent, Fragment, useState } from 'react';
import '../styles/Destination.css'
import * as  assests from '../utils/assets'
import { destinations } from "../data.js"

// styles
let labelS: object = { fontSize: assests.TYPOGRAPHY.subheaders.subheading3, fontFamily: "Barlow Condensed" }
let titleS: object = { fontFamily: "Barlow Condensed" }

const Destination = () => {
    const [current, setCurrent] = useState(0)

    const handleCurrentChange = (e: ChangeEvent<HTMLInputElement>) => {
        let val = e.target.value
        return console.log('the cuurent index change ', val)
    }
    const showXPlanet = () => {

    }
    return (
        <div className="container">

            {/* LEFT */}
            <div className="left">
                <h1 className="title" style={titleS} >
                    <span id="num" style={{ marginRight: "20px", fontFamily: "Barlow Condensed" }}>01</span>
                    Pick your destination
                </h1>
                <img src={destinations[current].images.png} alt="PLanet img" id="img" />
            </div>

            {/* RIGHT */}
            <div className="right">
                <div className="info-container">

                    <form className="slider">
                        {destinations.map((planet, index) => {
                            return (
                                <Fragment key={index}>
                                    <input id={planet['name']} type="text" hidden />
                                    <label onClick={() => setCurrent(index)} htmlFor="moon" style={labelS}>{planet["name"].toUpperCase()}</label>
                                </Fragment>
                            )
                        }
                        )}
                    </form>
                    <div className="text">
                        <h1 className="a" style={{ fontSize: assests.TYPOGRAPHY.headers.Heading2, fontFamily: "Bellefair" }} >{destinations[current].name.toLocaleUpperCase()}</h1>
                        <p className="par" style={{ fontFamily: "Barlow", fontSize: assests.TYPOGRAPHY.subheaders.subheading3 }}>
                            {destinations[current].description}
                        </p>
                    </div>
                    <hr id="sprt-line" />
                    <div className="planet">
                        <div className="distance">
                            <span>AVG. DISTANCE</span>
                            <h1 style={{ fontFamily: "Bellefair", fontSize: assests.TYPOGRAPHY.headers.Heading5 }}>{destinations[current].distance.toLocaleUpperCase()} </h1>
                        </div>
                        <div className="trvl-time">
                            <span>EST. TRAVEL TIME</span>
                            <h1 style={{ fontFamily: "Bellefair", fontSize: assests.TYPOGRAPHY.headers.Heading5 }}>{destinations[current].travel.toLocaleUpperCase()} </h1>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )

}

export default Destination;
