import React, { Fragment, useState } from "react"
import { technology } from "../data"
import "../styles/Technology.css"



const Technology = () => {
    const [current, setCurrent] = useState(0)

    return (
        <div className="tech-container">
            {/* LEFT */}
            <div className="left">
                <h1 className="title">03  SPACE LUNCH 101</h1>
                <div className="termo">
                    <form className="left">
                        {technology.map((tech, index) => {
                            return (
                                <Fragment key={index}>

                                    <input type="text" name="inp" id="inp" hidden />
                                    <label htmlFor="inp">{index}</label>
                                </Fragment>
                            )
                        })}
                    </form>
                    <div className="right">
                        <div className="content">
                            <h1 className="header">{technology[current].name.toUpperCase()}</h1>
                            <h1 className="header">{technology[current].description}</h1>
                        </div>
                    </div>
                </div>
            </div>
            {/* RIGHT */}
            <div className="crew-right">
                <img src={technology[current].images.portrait} alt="" className="spaceVehicle" />
            </div>
        </div>
    )
}
export default Technology
