import React, { Fragment, useState } from 'react';
import '../styles/Destination.css'
import * as  assests from '../utils/assets'
import { destinations } from "../data.js"

let imgT: string = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSed9KXasqN7dg_kOK2JeGHr0_r8EB4E9awRpqg_Eq66lTPMFArhwDbeXdK7HA_upxNYMc&usqp=CAU"

let testtext: string = 'and so many text'

const Destination = () => {
    const [current, setCurrent] = useState(0)

    const handleCurrentChange = () => {

    }

    return (
        <div className="container">

            {/* LEFT */}
            <div className="left">
                <h1 className="title">
                    <span id="num" style={{ marginRight: "20px" }}>01</span>
                    Pick your destination
                </h1>
                <img src={imgT} alt="PLanet img" className="planet" />
            </div>

            {/* RIGHT */}
            <div className="right">
                <div className="p-info-container">


                    <form className="slider">
                        {destinations.map((planet, index) => {

                            return (

                                <Fragment>
                                    <input id={planet['name']} type="text" hidden value={index} onChange={handleCurrentChange} />
                                    <label htmlFor="moon" style={{ fontSize: assests.TYPOGRAPHY.subheaders.subheading3, fontFamily: "Barlow Condensed" }}>{planet["name"].toUpperCase()}</label>
                                </Fragment>
                            )
                        }
                        )}
                    </form>

                    <div className="text">
                        <h1 className="a" style={{ fontSize: assests.TYPOGRAPHY.headers.Heading2, fontFamily: "Bellefair" }} >MOON</h1>
                        <p className="par" style={{ fontSize: assests.TYPOGRAPHY.subheaders.subheading3 }}>
                            {testtext.repeat(20)}
                        </p>
                    </div>

                    <hr />

                    <div className="plenet">
                        <div className="distance">
                            <span>AVG. DISTANCE</span>
                            <h1>384,400 KM</h1>
                        </div>
                        <div className="trvl-time">
                            <span>EST. TRAVEL TIME</span>
                            <h1>3 DAYS</h1>
                        </div>
                    </div>

                </div>
            </div>
        </div >
    )

}

export default Destination;
