import '../styles/navbar.css';
import logo from "../shared/logo.svg"
import { Link, Outlet } from 'react-router-dom';
import * as assets from "../utils/assets"
const navigationOptions: string[] = ["Home", "Destinations", "Crew", "Tech"]

//  to be refactored *
function NavBar() {
    return (
        <div className="nv-container">
            <div className="left">
                <img className="logo" src={logo} height="50" alt="1" />
                <hr className='hori-line' />
            </div>

            <div className="right">
                {navigationOptions.map((option, index) => {
                    return (
                        <Link to={option == "Home" ? "/" : option.toLocaleLowerCase()}>
                            <div className="option" style={{ textDecoration: "none", borderBottom: "2px solid #FFF" }}>
                                <span className='option-name' style={{ fontSize: assets.TYPOGRAPHY.subheaders.subheading3 }}>
                                    <b>0{index + 1}H</b>
                                    &nbsp;
                                           {option}
                                </span>
                            </div>
                        </Link>
                    )
                })}
            </div>

        </div>
    );
}

export default NavBar;
