import '../styles/Crew.css';
import * as assets from "../utils/assets"
import { crew } from "../data"
import { useState } from 'react';


// styles
let titleStyle: object = { fontSize: assets.TYPOGRAPHY.subheaders.subheading1 }
let nameStyle: object = { fontSize: assets.TYPOGRAPHY.headers.Heading3 }
let bioStyle: object = { fontSize: assets.TYPOGRAPHY.subheaders.subheading4 }

function Crew() {
    const [current, setCurrent] = useState(0)
    return (
        <div className="crew-container">
            <div className="left">
                <h1 className="barlow400" style={titleStyle}> <b id='num'>02</b> MEET YOUR CREW</h1>

                <div className="member-info">
                    <h1 className="role barlow400">{crew[0].role}</h1>
                    <h1 className="name" style={nameStyle}>{crew[0].name}</h1>
                    <p className="bio" style={bioStyle}>{crew[0].bio}</p>
                </div>
                <div className='navigator'>
                    {"navigator".toLocaleUpperCase()}
                </div>
            </div>
            <div className="crew-right">
                <img id="crewimg" src={crew[current].images.png} alt="his/her image" />
            </div>
        </div>
    );
}

export default Crew;
